<template>
  <svg :view-box="viewBox" :class="icon.class">
    <title v-if="title">{{ title }}</title>
    <desc v-if="desc">{{ desc }}</desc>
    <use :href="icon.url" />
  </svg>
</template>

<script setup lang="ts">
import { ref } from 'vue'
import { useSprite } from '../composables/useSprite'

const props = defineProps({
  name: {
    type: String,
    required: true
  },
  title: {
    type: String,
    default: null
  },
  desc: {
    type: String,
    default: null
  },
  viewBox: {
    type: String,
    default: null,
    validator (value: string) {
      return value.split(' ').every((v) => {
        return !isNaN(parseInt(v))
      })
    }
  }
})

const icon = ref({
  url: '',
  class: ''
})

icon.value = await useSprite(props.name)
</script>
